import React, { useState } from 'react';
import { GoogleMap, DirectionsRenderer, useJsApiLoader } from '@react-google-maps/api';

const containerStyle = {
    width: '100%',
    height: '400px'
};

const center = {
    lat: 41.5847,  // Purdue Northwest (Hammond) center
    lng: -87.4952
};

function DrivingRoute() {
    const { isLoaded } = useJsApiLoader({
        googleMapsApiKey: 'AIzaSyAx7-WecbbIOx8S7vCgaz64xB2yWfvpzgw' // Replace with your real key
    });

    const [origin, setOrigin] = useState('');
    const [destination, setDestination] = useState('');
    const [directions, setDirections] = useState(null);

    const handleGetRoute = () => {
        const directionsService = new window.google.maps.DirectionsService();
        directionsService.route(
            {
                origin,
                destination,
                travelMode: window.google.maps.TravelMode.DRIVING,
            },
            (result, status) => {
                if (status === window.google.maps.DirectionsStatus.OK) {
                    setDirections(result);
                } else {
                    console.error(`error fetching directions ${result}`);
                }
            }
        );
    };

    return (
        <div style={{ padding: 20 }}>
            <h2>Driving Directions</h2>
            <input
                type="text"
                placeholder="Enter your home address"
                value={origin}
                onChange={e => setOrigin(e.target.value)}
                style={{ marginRight: '10px' }}
            />
            <input
                type="text"
                placeholder="Enter event address"
                value={destination}
                onChange={e => setDestination(e.target.value)}
                style={{ marginRight: '10px' }}
            />
            <button onClick={handleGetRoute}>Get Route</button>

            {isLoaded && (
                <GoogleMap
                    mapContainerStyle={containerStyle}
                    center={center}
                    zoom={10}
                >
                    {directions && <DirectionsRenderer directions={directions} />}
                </GoogleMap>
            )}
        </div>
    );
}

export default DrivingRoute;
