import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [events, setEvents] = useState([]);
    const [selectedEvent, setSelectedEvent] = useState('');
    const [mapUrl, setMapUrl] = useState('');
    const [parkingSuggestion, setParkingSuggestion] = useState('');
    const [destination, setDestination] = useState('Purdue University Northwest');
    const [registeredEvents, setRegisteredEvents] = useState([]);
    const [selectedRegisteredEvent, setSelectedRegisteredEvent] = useState('');


    const [studentInfo, setStudentInfo] = useState({
        firstName: '',
        middleName: '',
        lastName: '',
        studentID: ''
    });

    const [newEvent, setNewEvent] = useState({
        event_name: '',
        abstract: '',
        event_date: '',
        location: '',
        cost: ''
    });

    useEffect(() => {
        fetchEvents();
    }, []);

    const fetchEvents = () => {
        axios.get('http://localhost:5000/events')
            .then(response => setEvents(response.data))
            .catch(error => console.error('Error fetching events', error));
    };

    

    const handleRegister = () => {
        if (!selectedEvent) {
            alert('Please select an event.');
            return;
        }

        axios.post('http://localhost:5000/register', {
            ...studentInfo,
            eventID: selectedEvent
        })
            .then(response => {
                alert(response.data.message);
                setStudentInfo({ firstName: '', middleName: '', lastName: '', studentID: '' });
                setSelectedEvent('');
            })
            .catch(error => console.error('Error registering student', error));
    };

    const handleAddEvent = () => {
        axios.post('http://localhost:5000/add-event', newEvent)
            .then(response => {
                alert(response.data.message);
                setNewEvent({ event_name: '', abstract: '', event_date: '', location: '', cost: '' });
                fetchEvents(); // Refresh events list
            })
            .catch(error => console.error('Error adding event', error));
    };

    const handleEventSelect = (e) => {
        const selectedId = e.target.value;
        setSelectedEvent(selectedId);

        const event = events.find(ev => ev.id.toString() === selectedId);

        if (event) {
            // Set destination from event location
            if (event.location && event.location.trim() !== '') {
                setDestination(event.location);
            } else {
                setDestination('Purdue University Northwest');
            }

            // Parking suggestion (optional)
            const lowerLocation = event.location.toLowerCase();
            if (lowerLocation.includes('hammond')) {
                setParkingSuggestion('Use Hammond Campus Parking Garage');
            } else if (lowerLocation.includes('westville')) {
                setParkingSuggestion('Use Westville Lot 6');
            } else {
                setParkingSuggestion('General PNW Parking Area');
            }
        }
    };



    const handleGetDirections = () => {
        if (!navigator.geolocation) {
            alert('Geolocation is not supported by your browser');
            return;
        }

        navigator.geolocation.getCurrentPosition(
            (position) => {
                const { latitude, longitude } = position.coords;
                const origin = `${latitude},${longitude}`;

                const url = `https://www.google.com/maps/embed/v1/directions?key=AIzaSyAx7-WecbbIOx8S7vCgaz64xB2yWfvpzgw&origin=${origin}&destination=${encodeURIComponent(destination)}`;

                setMapUrl(url);
            },
            (error) => {
                console.error('Error getting location:', error);
                alert('Unable to retrieve your location');
            }
        );
    };


    return (
        <div style={{ padding: 20 }}>
            <h1>PNW Student Life Events</h1>

            <h2>Events List</h2>
            <ul>
                {events.map(event => (
                    <li key={event.id}>
                        <strong>{event.event_name}</strong> — {event.event_date} — {event.location}
                    </li>
                ))}
            </ul>

            <h2>Register for an Event</h2>
            <select onChange={handleEventSelect} value={selectedEvent}>
                <option value="">Select Event</option>
                {events.map(event => (
                    <option key={event.id} value={event.id}>
                        {event.event_name}
                    </option>
                ))}
            </select>

            <input
                placeholder="First Name"
                value={studentInfo.firstName}
                onChange={e => setStudentInfo({ ...studentInfo, firstName: e.target.value })}
            />
            <input
                placeholder="Middle Name"
                value={studentInfo.middleName}
                onChange={e => setStudentInfo({ ...studentInfo, middleName: e.target.value })}
            />
            <input
                placeholder="Last Name"
                value={studentInfo.lastName}
                onChange={e => setStudentInfo({ ...studentInfo, lastName: e.target.value })}
            />
            <input
                placeholder="Student ID"
                value={studentInfo.studentID}
                onChange={e => setStudentInfo({ ...studentInfo, studentID: e.target.value })}
            />
            <button onClick={handleRegister}>Register</button>

            {parkingSuggestion && (
                <div style={{ marginTop: 10 }}>
                    <strong>Suggested Parking Lot:</strong> {parkingSuggestion}
                </div>
            )}

            <h2>Add a New Event</h2>
            <input
                placeholder="Event Name"
                value={newEvent.event_name}
                onChange={e => setNewEvent({ ...newEvent, event_name: e.target.value })}
            />
            <input
                placeholder="Abstract"
                value={newEvent.abstract}
                onChange={e => setNewEvent({ ...newEvent, abstract: e.target.value })}
            />
            <input
                type="date"
                value={newEvent.event_date}
                onChange={e => setNewEvent({ ...newEvent, event_date: e.target.value })}
            />
            <input
                placeholder="Location"
                value={newEvent.location}
                onChange={e => setNewEvent({ ...newEvent, location: e.target.value })}
            />
            <input
                placeholder="Cost"
                type="number"
                value={newEvent.cost}
                onChange={e => setNewEvent({ ...newEvent, cost: e.target.value })}
            />
            <button onClick={handleAddEvent}>Add Event</button>

            <h2>Driving Directions</h2>

            <button onClick={handleGetDirections}>Get Directions From My Location</button>

            {mapUrl && (
                <div style={{ marginTop: 20 }}>
                    <iframe
                        src={mapUrl}
                        width="100%"
                        height="450"
                        allowFullScreen=""
                        loading="lazy"
                        title="Driving Directions"
                    ></iframe>
                </div>
            )}
        </div>
    );
}

export default App;
