import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Button, MenuItem, Select, InputLabel, FormControl, Card, CardContent, Typography, Grid, Snackbar, Alert } from '@mui/material';

function App() {
    const [events, setEvents] = useState([]);
    const [selectedEvent, setSelectedEvent] = useState(null);
    const [mapUrl, setMapUrl] = useState('');
    const [parkingSuggestion, setParkingSuggestion] = useState('');
    const [destination, setDestination] = useState('Purdue University Northwest');
    const [registeredEvents, setRegisteredEvents] = useState([]);
    const [selectedRegisteredEvent, setSelectedRegisteredEvent] = useState(null);
    const [searchStudentID, setSearchStudentID] = useState('');
    const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

    const [studentInfo, setStudentInfo] = useState({
        firstName: '',
        middleName: '',
        lastName: '',
        studentID: ''
    });

    const [newEvent, setNewEvent] = useState({
        event_name: '',
        abstract: '',
        event_date: '',
        location: '',
        cost: '',
        time_from: '',
        time_to: ''
    });

    useEffect(() => { fetchEvents(); }, []);

    const fetchEvents = () => {
        axios.get('http://localhost:5000/events')
            .then(response => setEvents(response.data))
            .catch(error => console.error('Error fetching events', error));
    };

    const fetchRegisteredEvents = () => {
        if (!searchStudentID) {
            alert('Please enter your Student ID first.');
            return;
        }

        axios.get(`http://localhost:5000/my-registrations/${searchStudentID}`)
            .then(response => setRegisteredEvents(response.data))
            .catch(error => console.error('Error fetching registered events', error));
    };

    const handleRegister = () => {
        if (!selectedEvent) {
            alert('Please select an event.');
            return;
        }

        axios.post('http://localhost:5000/register', { ...studentInfo, eventID: selectedEvent.id })
            .then(response => {
                setSnackbar({ open: true, message: response.data.message, severity: 'success' });
                setStudentInfo({ firstName: '', middleName: '', lastName: '', studentID: '' });
                setSelectedEvent(null);
            })
            .catch(error => {
                console.error('Error registering student', error);
                setSnackbar({ open: true, message: 'Registration failed', severity: 'error' });
            });
    };

    const handleAddEvent = () => {
        axios.post('http://localhost:5000/add-event', newEvent)
            .then(response => {
                setSnackbar({ open: true, message: response.data.message, severity: 'success' });
                setNewEvent({ event_name: '', abstract: '', event_date: '', location: '', cost: '', time_from: '', time_to: '' });
                fetchEvents();
            })
            .catch(error => {
                console.error('Error adding event', error);
                setSnackbar({ open: true, message: 'Adding event failed', severity: 'error' });
            });
    };

    const handleEventSelect = (e) => {
        const event = events.find(ev => ev.id.toString() === e.target.value);
        setSelectedEvent(event);

        if (event) {
            if (event.location && event.location.trim() !== '') {
                setDestination(event.location);
            } else {
                setDestination('Purdue University Northwest');
            }

            const lowerLocation = event.location.toLowerCase();
            if (lowerLocation.includes('hammond')) {
                setParkingSuggestion('Use Hammond Campus Parking Garage');
            } else if (lowerLocation.includes('westville')) {
                setParkingSuggestion('Use Westville Lot 6');
            } else {
                setParkingSuggestion('General PNW Parking Area');
            }
        }
    };

    const handleGetDirectionsFromRegisteredEvent = () => {
        if (!selectedRegisteredEvent) {
            alert('Please select an event');
            return;
        }

        const event = registeredEvents.find(ev => ev.id.toString() === selectedRegisteredEvent);

        if (!event) {
            alert('Please select a registered event first.');
            return;
        }

        if (!navigator.geolocation) {
            alert('Geolocation not supported');
            return;
        }

        navigator.geolocation.getCurrentPosition(
            (position) => {
                const { latitude, longitude } = position.coords;
                const origin = `${latitude},${longitude}`;
                const dest = event.location ? event.location : 'Purdue University Northwest';

                const url = `https://www.google.com/maps/embed/v1/directions?key=AIzaSyAx7-WecbbIOx8S7vCgaz64xB2yWfvpzgw&origin=${origin}&destination=${encodeURIComponent(dest)}`;

                setMapUrl(url);
            },
            (error) => {
                console.error('Error getting location:', error);
                alert('Unable to retrieve your location');
            }
        );
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        return new Date(dateString).toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });
    };

    return (
        <div style={{ padding: 30 }}>
            <Typography variant="h3" align="center" gutterBottom>PNW Student Life Events</Typography>

            <Grid container spacing={4}>
                <Grid item xs={12}>
                    <Card>
                        <CardContent>
                            <Typography variant="h5">Events List</Typography>
                            {events.map(event => (
                                <Typography key={event.id} variant="body2">
                                    {event.event_name} â€” {formatDate(event.event_date)}
                                    {event.time_from && event.time_to ? ` â€” ${event.time_from} to ${event.time_to}` : ''}
                                    â€” {event.location}
                                </Typography>
                            ))}
                        </CardContent>
                    </Card>
                </Grid>

                <Grid item xs={12} md={6}>
                    <Card>
                        <CardContent>
                            <Typography variant="h5">Register for an Event</Typography>
                            <FormControl fullWidth margin="normal">
                                <InputLabel>Select Event</InputLabel>
                                <Select value={selectedEvent?.id || ''} onChange={handleEventSelect}>
                                    {events.map(event => (
                                        <MenuItem key={event.id} value={event.id}>{event.event_name}</MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                            <TextField label="First Name" fullWidth margin="normal" value={studentInfo.firstName} onChange={e => setStudentInfo({ ...studentInfo, firstName: e.target.value })} />
                            <TextField label="Middle Name" fullWidth margin="normal" value={studentInfo.middleName} onChange={e => setStudentInfo({ ...studentInfo, middleName: e.target.value })} />
                            <TextField label="Last Name" fullWidth margin="normal" value={studentInfo.lastName} onChange={e => setStudentInfo({ ...studentInfo, lastName: e.target.value })} />
                            <TextField label="Student ID" fullWidth margin="normal" value={studentInfo.studentID} onChange={e => setStudentInfo({ ...studentInfo, studentID: e.target.value })} />
                            <Button fullWidth variant="contained" onClick={handleRegister}>Register</Button>

                            {parkingSuggestion && (
                                <Typography variant="body2" style={{ marginTop: 10 }}>
                                    <strong>Suggested Parking Lot:</strong> {parkingSuggestion}
                                </Typography>
                            )}
                        </CardContent>
                    </Card>
                </Grid>

                <Grid item xs={12} md={6}>
                    <Card>
                        <CardContent>
                            <Typography variant="h5">Add a New Event</Typography>
                            <TextField label="Event Name" fullWidth margin="normal" value={newEvent.event_name} onChange={e => setNewEvent({ ...newEvent, event_name: e.target.value })} />
                            <TextField label="Abstract" fullWidth margin="normal" value={newEvent.abstract} onChange={e => setNewEvent({ ...newEvent, abstract: e.target.value })} />
                            <TextField label="Event Date" type="date" InputLabelProps={{ shrink: true }} fullWidth margin="normal" value={newEvent.event_date} onChange={e => setNewEvent({ ...newEvent, event_date: e.target.value })} />
                            <TextField label="Location" fullWidth margin="normal" value={newEvent.location} onChange={e => setNewEvent({ ...newEvent, location: e.target.value })} />
                            <TextField label="Cost" type="number" fullWidth margin="normal" value={newEvent.cost} onChange={e => setNewEvent({ ...newEvent, cost: e.target.value })} />
                            <TextField label="Start Time (e.g. 10:00 AM)" fullWidth margin="normal" value={newEvent.time_from} onChange={e => setNewEvent({ ...newEvent, time_from: e.target.value })} />
                            <TextField label="End Time (e.g. 2:00 PM)" fullWidth margin="normal" value={newEvent.time_to} onChange={e => setNewEvent({ ...newEvent, time_to: e.target.value })} />
                            <Button fullWidth variant="contained" color="success" onClick={handleAddEvent}>Add Event</Button>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid item xs={12}>
                    <Card>
                        <CardContent>
                            <Typography variant="h5">Your Registered Events</Typography>
                            <TextField label="Enter Your Student ID" fullWidth margin="normal" value={searchStudentID} onChange={e => setSearchStudentID(e.target.value)} />
                            <Button fullWidth variant="outlined" onClick={fetchRegisteredEvents}>Load My Registered Events</Button>
                            <FormControl fullWidth margin="normal">
                                <InputLabel>Select Registered Event</InputLabel>
                                <Select value={selectedRegisteredEvent || ''} onChange={e => setSelectedRegisteredEvent(e.target.value)}>
                                    {registeredEvents.map(event => (
                                        <MenuItem key={event.id} value={event.id}>{event.event_name} â€” {formatDate(event.event_date)}</MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                            <Button fullWidth variant="contained" color="secondary" onClick={handleGetDirectionsFromRegisteredEvent}>Get Directions to This Event</Button>
                        </CardContent>
                    </Card>
                </Grid>

                {mapUrl && (
                    <Grid item xs={12}>
                        <Card>
                            <CardContent>
                                <Typography variant="h5">Driving Directions</Typography>
                                <iframe
                                    src={mapUrl}
                                    width="100%"
                                    height="450"
                                    style={{ border: 0 }}
                                    allowFullScreen=""
                                    loading="lazy"
                                    title="Directions Map"
                                ></iframe>
                            </CardContent>
                        </Card>
                    </Grid>
                )}
            </Grid>

            <Snackbar open={snackbar.open} autoHideDuration={4000} onClose={() => setSnackbar({ ...snackbar, open: false })}>
                <Alert severity={snackbar.severity} sx={{ width: '100%' }}>{snackbar.message}</Alert>
            </Snackbar>
        </div>
    );
}

export default App;
